import React, { useState } from "react";
import { nanoid } from "nanoid";
import NoteList from './components/NotesList'

const App = () => {
  const [notes, setNotes] = useState([
    { id: nanoid(), 
      text: "Hello 1!", 
      date: "20/10/2022" 
    },
    { id: nanoid(), 
      text: "Hello 2!", 
      date: "20/10/2022" 
    },
    { id: nanoid(), 
      text: "Hello! 3", 
      date: "12/10/2022" 
    },
    { id: nanoid(), 
      text: "Hello 4!", 
      date: "10/10/2022"
    },

  ]);

  const addNote = (text) => {
    const date = new Date();
    const newNote = {
      id: nanoid(),
      text: text,
      date: date.toLocaleDateString(),
    }
    const newNotes = [...notes, newNote];
    setNotes(newNotes);
  }

  const deleteNote = (id) => {
    const newNotes = notes.filter((note) => note.id !== id);
    setNotes(newNotes);
  }
  return (
    <div className='container'>
      <NoteList notes = {notes} handleAddNote={addNote}/>
    </div>
  )
}

export default App
