import React, { useEffect, useState } from "react";
import { nanoid } from "nanoid";
import NoteList from "./components/NotesList";
import SearchBar from "./components/SearchBar";
import Header from "./components/Header";

const App = () => {
  const [notes, setNotes] = useState([
    { id: nanoid(), text: "Hello 1!", date: "20/10/2022" },
    { id: nanoid(), text: "Hello 2!", date: "20/10/2022" },
    { id: nanoid(), text: "Hello! 3", date: "12/10/2022" },
    { id: nanoid(), text: "Hello 4!", date: "10/10/2022" },
  ]);

  const [searchText, setSearchText] = useState("");

  const [darkMode, setDarkMode] = useState(false);

  // retrieve notes that saved in local storage when the app loads
  // when array is empty it means
  useEffect(()=>{

  },[])
  // save data to a local storage with a key
  useEffect(()=>{
    localStorage.setItem('mint-notes-app-data', JSON.stringify(notes));
  }, [notes]);

  const addNote = (text) => {
    const date = new Date();
    const newNote = {
      id: nanoid(),
      text: text,
      date: date.toLocaleDateString(),
    };
    const newNotes = [...notes, newNote];
    setNotes(newNotes);
  };

  const deleteNote = (id) => {
    const newNotes = notes.filter((note) => note.id !== id);
    setNotes(newNotes);
  };
  return (
    <div className={`${darkMode && 'dark-mode'}`}>
      {" "}
      <div className="container">
        <Header handleToggleDarkMode={setDarkMode} />
        <SearchBar handleSearchNote={setSearchText} />
        <NoteList
          notes={notes.filter((note) =>
            note.text.toLowerCase().includes(searchText)
          )}
          handleAddNote={addNote}
          handleDeleteNote={deleteNote}
        />
      </div>
    </div>
  );
};

export default App;
